(()=>{"use strict";new class{constructor(){this.date=new Date,this.currentDay=this.date.getDate(),this.timePosition=this.getTimePosition(),this.datePosition=this.getDatePosition(),this.init()}init(){this.insertTimeValue(),this.insertDateValue(),setInterval((()=>{this.date=new Date,this.insertTimeValue(),this.currentDay!==this.date.getDate()&&(this.currentDay=this.date.getDate(),this.insertDateValue())}),1e3)}getTimePosition(){return document.querySelector("time")}getDatePosition(){return document.querySelector("date")}insertTimeValue(){this.timePosition.textContent=this.date.toLocaleTimeString()}insertDateValue(){this.datePosition.textContent=this.date.toLocaleDateString("en-En",{weekday:"long",month:"long",day:"numeric",timeZone:"UTC"})}}})();